versions.equation_logic_text:
var calc_output = [];

var problems = parseFloat(problems);
var risk = parseFloat(risk);
var ordered = parseFloat(ordered);
var results = parseFloat(results);
var notes = parseFloat(notes);
var hist = parseFloat(hist);
var interp = parseFloat(interp);
var ext = parseFloat(ext);

var p_level;
var p_group;

if (problems == 13) {
    p_level = 2;
    p_group = 'Minimal';
} else if (problems <= 12 && problems >= 8) {
    p_level = 3;
    p_group = 'Low';
;} else if (problems <= 7 && problems >= 3) {
    p_level = 4;
    p_group = 'Moderate';
} else {
    p_level = 5;
    p_group = 'High';
}

var r_level;
var r_group;

if (risk == 1) {
    r_level = 2;
    r_group = 'Minimal';
} else if (risk == 2) {
    r_level = 3;
    r_group = 'Low';
} else if (risk == 3) {
    r_level = 4;
    r_group = 'Moderate';
} else {
    r_level = 5;
    r_group = 'High';
}

var d_level;
var d_group;
var sum = ordered + results + notes + hist;
var sum2 = ordered + results + notes;

if ((sum >= 3 && interp == 1) || (sum == 3 && ext == 1) || (interp == 1 && ext == 1)) {
    d_level = 5;
    d_group = 'Extensive';
} else if (sum >= 3 || interp == 1 || ext == 1) {
    d_level = 4;
    d_group = 'Moderate';
} else if (sum2 == 2 || hist == 1) {
    d_level = 3;
    d_group = 'Limited';
} else if (sum2 <= 1 && hist == 0 && interp == 0 && ext == 0) {
    d_level = 2;
    d_group = 'Minimal';
}

var max2 = [p_level, r_level, d_level].sort(function(a, b) { 
    return b - a; 
})[1];

calc_output.push({
    name: 'Level',
    value: max2,
    value_text: 'Level of Service',
    message: 'Problems: ' + p_group + ' (' + p_level + ') <br/> Risk: ' + r_group + ' (' + r_level + ') <br/> Data: ' + d_group + ' (' + d_level + ')'
});
