var egfr2 = parseFloat(egfr2) || 0;

var msg;

if (age == 0 || disease == 0 || symptoms == 0 || egfr == 0 || impairment == 0 || (criteria == -1 && egfr2 == 0)) {
    msg = 'Paxlovid may not be indicated in this patient.<br/><br/>Refer to NIH treatment guidelines.';
} 

if (criteria != -1 && egfr2 != 0) {
    if (egfr2 == 1) {
        msg = 'Paxlovid may be a management option for this patient if they are in the outpatient setting or if they are hospitalized for a reason other than COVID-19 infection.<br/><br/>Prescribe nirmatrelvir 300mg w/ ritonavir 100mg PO BID for 5 days.';
    }
    else if (egfr2 == 2) {
        msg = 'Paxlovid may be a management option for this patient if they are in the outpatient setting or if they are hospitalized for a reason other than COVID-19 infection.<br/><br/>Prescribe nirmatrelvir 150mg w/ ritonavir 100mg PO BID for 5 days.'
    }
}

calc_output.push({
