var calc_output = [];

//user inputs
var score = parseFloat(snore) + parseFloat(tired) + parseFloat(observed) + parseFloat(pressure) + parseFloat(bmi) + parseFloat(age) + parseFloat(neck) + parseFloat(gender);

//interpretation
var result;
if(score < 3){
	result = 'Low';
}else{
	result = 'High';
}

//results
calc_output.push({
    name: 'mini',
    value: result,
    value_text: '',
    message: 'Risk for OSA'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'STOP-BANG'
});
calc_output.push({
    name: 'Result',
    value: result,
    value_text: 'risk of OSA',
    message: ''
});
